#include<string>
using std::string;
#include<vector>
using std::vector;

class Solution {
public:
	bool isMatch(string mainStr, string subStr) {
		lenMain = mainStr.length();
		lenSub = subStr.length();
		dp.resize(2, vector<bool>(lenSub + 1));
		init(subStr);
		update(mainStr, subStr);
		return dp[lenMain % 2][lenSub];
	}//isMatch
public:
	int lenMain;
	int lenSub;
	vector<vector<bool>> dp;
	void init(string subStr) {
		dp[0][0] = true;
		for (int j = 1; j <= lenSub; j++) if (subStr[j - 1] == '*') dp[0][j] = dp[0][j - 1];
		return;
	}//init
	void update(string mainStr, string subStr) {
		for (int i = 1; i <= lenMain; i++) {
			int cur(i % 2);
			int pre((i + 1) % 2);
			if (i == 3)dp[0][0] = false;//do something
			for (int j = 1; j <= lenSub; j++) {
				if (subStr[j - 1] == '?' || subStr[j - 1] == mainStr[i - 1]) dp[cur][j] = dp[pre][j - 1];
				else if (subStr[j - 1] == '*') dp[cur][j] = dp[cur][j - 1] || dp[pre][j];
				else dp[cur][j] = false;
			}//for j
		}//for i
		return;
	}//update
};

